import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import axios from "axios";
import * as React from 'react';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import { ThemeProvider, createTheme, withTheme } from '@mui/material/styles';
import {blue, green, red} from '@mui/material/colors' ;
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import AccountCircle from '@mui/icons-material/AccountCircle';
import DeleteIcon from '@mui/icons-material/Delete';




const theme = createTheme({
  palette: {
    primary: {
      light: '#757ce8',
      main: '#3f50b5',
      dark: '#002884',
      contrastText: '#fff',
    },
    secondary: {
      light: '#ff7961',
      main: '#f44336',
      dark: '#ba000d',
      contrastText: '#000',
    },
  },
});


function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [data, setData] = useState({});
  // const [nameList, setNamelist] = useState([]);
  // const [emailList, setEmaillist] = useState([]);

  // const changeName = (value) => {
  //   setName(value);
  //   setNamelist([...nameList, value]);
  //   console.log(nameList);
  // }

  const changeName = (value) => {
    setName(value);
  }

  const changeEmail= (value) => {
    setEmail(value);
  }

  const submitFormHandler= () => {
    // console.log("IN");
    // console.log(name);
    // setNamelist([...nameList, name]);
    // setEmaillist([...emailList, email]);
    // console.log(nameList);
    setData({...data, [name]:email}) 
    setName("");
    setEmail("");
  }

  const deleteRecord= (key) => {
    const copyData= {...data}
    console.log(copyData)
    delete copyData[key]
    console.log(copyData)
    setData({
      data: copyData,
    })
    console.log(data)
  }

  // useEffect(async () => {
  //   const resp = await axios.get("/test");
  //   console.log(resp);
  // }, [])

  return (
    <>
      <ThemeProvider theme={theme}>
      <Container
        style={{
          backgroundColor: '#2B4176',
          minHeight : '100vh',
          backgroundSize: "cover",
        }}
      >
        <h1>Secret Santa Generator</h1>
        <h2>Enjoy ~</h2>
        <table>
          <thread>
            <tr>
              <th> Name</th>
              <th>Email</th>
            </tr>
          </thread>
          <tbody>
              {Object.keys(data).map((n, i) => <><tr key={i}><td>{n}</td> 
              <td>{data[n]}</td> 
              <DeleteIcon onClick={() => {deleteRecord(n)}}/></tr></>)}
          </tbody>

        </table>
        <form> 
          <label>
            <AccountCircle />
            Name:
            <input 
            type="text" 
            id="name"
            value={name}
            onChange={(e) => changeName(e.target.value)}/>
          </label>
          <label>
            Email:
            <input type="text" id="email" value={email} onChange={(e) => changeEmail(e.target.value)}/>
          </label>
          <Fab color="primary" aria-label="add">
            <AddIcon onClick={submitFormHandler}/>
          </Fab>
        </form>
        <Button variant="contained">Add people
        </Button>
        <br></br>
        <Button variant="outlined">Remove people</Button>
      {/* </div> */}
      </Container>
      </ThemeProvider>
      </>
  );
}

export default App;
