import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import axios from "axios";
import * as React from 'react';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import { ThemeProvider, createTheme, withTheme } from '@mui/material/styles';
import {blue, green, red} from '@mui/material/colors' ;
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import AccountCircle from '@mui/icons-material/AccountCircle';
import DeleteIcon from '@mui/icons-material/Delete';




const theme = createTheme({
  palette: {
    primary: {
      light: '#757ce8',
      main: '#3f50b5',
      dark: '#002884',
      contrastText: '#fff',
    },
    secondary: {
      light: '#ff7961',
      main: '#f44336',
      dark: '#ba000d',
      contrastText: '#000',
    },
  },
});


function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [data, setData] = useState({});
  const [result, setResult] = useState(null);

  const changeName = (value) => {
    setName(value);
  }

  const changeEmail= (value) => {
    setEmail(value);
  }

  const submitFormHandler= () => {
    setData({...data, [name]:email}) 
    setName("");
    setEmail("");
  }

  const deleteRecord= (key) => {
    const copyData= {...data}
    delete copyData[key]
    setData(copyData)
  }

  const sendDataBE = () => {
    const json_data = JSON.stringify(data)
    axios.post('/generator', data)
        .then(response => {
              console.log(response);
              setResult(response.data);
              console.log(result)
            })

  }
  // useEffect(async () => {
  //   const resp = await axios.get("/test");
  //   console.log(resp);
  // }, [])

  return (
    <>
      <ThemeProvider theme={theme}>
      <Container
        style={{
          backgroundColor: '#2B4176',
          minHeight : '100vh',
          backgroundSize: "cover",
        }}
      >
        <h1>Secret Santa Generator</h1>
        <h2>Enjoy ~</h2>
        <table>
          <thread>
            <tr>
              <th> Name</th>
              <th>Email</th>
              {result && <th> Secret Santa</th>}
            </tr>
          </thread>
          <tbody>
              {Object.keys(data).map((n, i) => {
              return <tr key={i}>
                        <td>{n}</td> 
                        <td>{data[n]}</td> 
                        {result ? <td>{result[n]}</td> : <DeleteIcon onClick={() => {deleteRecord(n)}}/>}
                    </tr>})}
          </tbody>

        </table>
        <form> 
          <label>
            <AccountCircle />
            Name:
            <input 
            type="text" 
            id="name"
            value={name}
            onChange={(e) => changeName(e.target.value)}/>
          </label>
          <label>
            Email:
            <input type="text" id="email" value={email} onChange={(e) => changeEmail(e.target.value)}/>
          </label>
          <Fab color="primary" aria-label="add">
            <AddIcon onClick={submitFormHandler}/>
          </Fab>
        </form>
        <Button variant="contained" onClick={sendDataBE}>Generate Now!
        </Button>
        <br></br>
      </Container>
      </ThemeProvider>
      </>
  );
}

export default App;
