import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import axios from "axios";
import * as React from 'react';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import { ThemeProvider, createTheme, withTheme } from '@mui/material/styles';
import {green, red} from '@mui/material/colors' ;

const theme = createTheme({
  palette: {
    primary: green,
    secondary: red,
  },
  components: {
    // Name of the component
    MuiButton: {
      styleOverrides: {
        // Name of the slot
        root: {
          // Some CSS
          fontSize: '1rem',
        },
      },
    },
  },
});


function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [nameList, setNamelist] = useState([]);
  const [emailList, setEmaillist] = useState([]);

  const changeName = (value) => {
    setEmail(value);
    setNamelist([...nameList, value]);
  }

  const changeEmail= (value) => {
    setEmail(value);
    setNamelist([...emailList, value]);
  }

  useEffect(async () => {
    const resp = await axios.get("/test");
    console.log(resp);
  }, [])

  return (
    <ThemeProvider theme={theme}>
      {/* <div className="App" style={{
        backgroundColor: '#2B4176',
        minHeight : '100%',
        width: '100%'
      }}> */}
      <Container
        name
        style={{
          backgroundColor: '#2B4176',
        }}
      >
        <h1>Secret Santa Generator</h1>
        <h2>Enjoy ~</h2>
        <form>
          <label>
            Name:
            <input 
            type="text" 
            id="name"
            onChange={(e) => changeName(e.target.value)}/>
          </label>
          <label>
            Email:
            <input type="text" id="email" onChange={(e) => changeEmail(e.target.value)}/>
          </label>
          <Button variant="contained"> Submit </Button>
        </form>
        <table>
          <p>{nameList}</p>
          <p>{emailList}</p>
        </table>
        <Button variant="contained">Add people
        </Button>
        <br></br>
        <Button variant="outlined">Remove people</Button>
      {/* </div> */}
      </Container>
    </ThemeProvider>
  );
}

export default App;
