import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
import axios from "axios";
import * as React from 'react';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import { ThemeProvider, createTheme, withTheme } from '@mui/material/styles';
import {blue, green, red} from '@mui/material/colors' ;
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';



const theme = createTheme({
  palette: {
    primary: {
      light: '#757ce8',
      main: '#3f50b5',
      dark: '#002884',
      contrastText: '#fff',
    },
    secondary: {
      light: '#ff7961',
      main: '#f44336',
      dark: '#ba000d',
      contrastText: '#000',
    },
  },
});


function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [nameList, setNamelist1] = useState([]);
  const [emailList, setEmaillist] = useState([]);

  // const changeName = (value) => {
  //   setName(value);
  //   setNamelist([...nameList, value]);
  //   console.log(nameList);
  // }

  const changeName = (value) => {
    setName(value);
  }

  const changeEmail= (value) => {
    setEmail(value);
    setEmaillist([...emailList, value]);
  }

  // const appendName = () => {
  //   setNamelist([...nameList, name]);
  //   setName("");
  //   console.log(nameList);
  // }
  function clearFields(event) {
    Array.from(event.target).forEach((e) => (e.value = ""));
  }
  function submitFormHandler(event) {
    // we don't want our page to refresh
    event.preventDefault();
    console.log("IN");
    console.log(name);
    console.log([...nameList, name])
    setNamelist1("bacd");
    console.log(nameList);
    // clearFields(event);
    setName("");
  }


  // useEffect(async () => {
  //   const resp = await axios.get("/test");
  //   console.log(resp);
  // }, [])

  return (
    <>
      <ThemeProvider theme={theme}>
      <Container
        style={{
          backgroundColor: '#2B4176',
          minHeight : '100vh',
          backgroundSize: "cover",
        }}
      >
        <h1>Secret Santa Generator</h1>
        <h2>Enjoy ~</h2>
        <div>
          <div>
           {/* {nameList.map((n, i) => <p key={i}>{n.name}</p>)} */}
          </div>
          <p>{emailList.map((e) => <p key={e.name}>{e.name}</p>)}</p>
        </div>
        <form> 
          <label>
            Name:
            <input 
            type="text" 
            id="name"
            value={name}
            onChange={(e) => changeName(e.target.value)}/>
          </label>
          <label>
            Email:
            <input type="text" id="email" onChange={(e) => changeEmail(e.target.value)}/>
          </label>
          <Fab color="primary" aria-label="add">
            <AddIcon onClick={submitFormHandler}/>
          </Fab>
        </form>
        <Button variant="contained">Add people
        </Button>
        <br></br>
        <Button variant="outlined">Remove people</Button>
      {/* </div> */}
      </Container>
      </ThemeProvider>
      </>
  );
}

export default App;
